window.addEventListener("load",function(){const t=this.document.querySelector(".modal"),e=document.querySelector(".modal .modal-header .modal-title"),n=document.querySelector(".modal .modal-text"),l=document.querySelector(".modal .modal-image img"),o=document.querySelector(".modal .modal-body .modal-content .modal-header .modal-button-close");function d(){l.src="",l.alt="",e.textContent="",n.textContent="",t.style.display="none",document.body.classList.remove("unscrolled")}document.addEventListener("click",o=>{if(o.target.classList.contains("display-project")){let d=o.target.parentElement.parentElement.children[0].children[1].getAttribute("src"),a=o.target.parentElement.parentElement.children[0].children[1].getAttribute("alt");l.src=d,l.alt=a,e.textContent=o.target.parentElement.children[0].textContent,n.textContent=o.target.parentElement.children[1].textContent,t.style.display="flex",document.body.classList.add("unscrolled")}}),o.onclick=d,t.onclick=function(t){t.target==this&&d()}});